INSERT INTO `hbxzs` VALUES (1,'环保创意','“光盘行动”，指就餐时倡导人们不浪费粮食，吃光盘子里的东西，吃不完的饭菜打包带走。2013年1月16首先由腾讯微博认证用户徐侠客（国土资源报副社长）在腾讯微博发起“光盘行动”，倡导网友珍惜粮食，加入行动。');
INSERT INTO `hbxzs` VALUES (2,'环保指南','1.&ldquo;绿色&rdquo;家居出行<br />\r\n2.回收利用垃圾<br />\r\n3.保护森林<br />\r\n4.珍惜淡水<br />\r\n5.保护野生动物<br />\r\n6.乐活生活');

