#
# Source for table hdlb
#

DROP TABLE IF EXISTS `hdlb`;
CREATE TABLE `hdlb` (
  `lbid` int(11) NOT NULL AUTO_INCREMENT,
  `lbmc` varchar(10) DEFAULT NULL COMMENT '类别名称',
  PRIMARY KEY (`lbid`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='活动类别';

#
# Dumping data for table hdlb
#

