INSERT INTO `hdlb` VALUES (3,'其它');

#
# Source for table htb
#

DROP TABLE IF EXISTS `htb`;
CREATE TABLE `htb` (
  `htid` int(11) NOT NULL AUTO_INCREMENT,
  `ftid` int(11) DEFAULT NULL COMMENT '主贴id',
  `htnr` text COMMENT '回帖内容',
  `htsj` datetime DEFAULT NULL COMMENT '回帖时间',
  `htr` int(11) DEFAULT NULL COMMENT '回帖人',
  `sfsh` bit(1) DEFAULT b'1' COMMENT '是否审核',
  PRIMARY KEY (`htid`),
  KEY `ztid` (`ftid`),
  KEY `htr` (`htr`)
) ENGINE=InnoDB AUTO_INCREMENT=36 DEFAULT CHARSET=utf8 COMMENT='回帖表';

