DROP TABLE IF EXISTS `hyb`;
CREATE TABLE `hyb` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `xm` varchar(20) DEFAULT NULL COMMENT '姓名',
  `xb` varchar(2) DEFAULT NULL COMMENT '性别',
  `xj` varchar(20) DEFAULT NULL COMMENT '系级',
  `lxdh` varchar(11) DEFAULT NULL COMMENT '联系电话',
  `dh` varchar(6) DEFAULT NULL COMMENT '校园短号',
  `hyzp` varchar(200) DEFAULT NULL COMMENT '会员照片',
  `bz` varchar(255) DEFAULT NULL COMMENT '备注',
  `zw` int(11) DEFAULT NULL COMMENT '职务',
  PRIMARY KEY (`id`),
  KEY `zw` (`zw`)
) ENGINE=InnoDB AUTO_INCREMENT=149 DEFAULT CHARSET=utf8 COMMENT='会员表';

