#
# Source for procedure Proc_GetHtzs
#

DROP PROCEDURE IF EXISTS `Proc_GetHtzs`;
CREATE DEFINER=`root`@`localhost` PROCEDURE `Proc_GetHtzs`()
    COMMENT '计算回帖总数'
BEGIN
declare t_ftid int(10);
declare t_htzs int(10);
 DECLARE  v_not_found int default 0;  /*游标结束控制变量*/
  /*定义游标：从htb表中读取所有行*/
  DECLARE  cur_htb CURSOR for    
    SELECT ftid
    FROM   htb;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_not_found=1;    

set t_htzs=0;
open cur_htb;
fetch cur_htb into t_ftid;
  while v_not_found=0 do      
     if t_ftid=48 then      
   set t_htzs= t_htzs+1;      
end if;
fetch cur_htb into t_ftid;/*读取下一行*/
end while;

select t_htzs;
update ftb set htzs=t_htzs where ftid=48;
close cur_htb;
END;


#
# Source for procedure Proc_GetTzzs
#

DROP PROCEDURE IF EXISTS `Proc_GetTzzs`;
CREATE DEFINER=`root`@`localhost` PROCEDURE `Proc_GetTzzs`()
    COMMENT '计算板块主题总数'
BEGIN
declare t_bkid int(10);
declare t_tzzs int(10);
 DECLARE  v_not_found int default 0;  /*游标结束控制变量*/
  /*定义游标：从ftb表中读取所有行*/
  DECLARE  cur_ftb CURSOR for    
    SELECT bkid
    FROM   ftb;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_not_found=1;    

set t_tzzs=0;
open cur_ftb;
fetch cur_ftb into t_bkid;
  while v_not_found=0 do      
     if t_bkid=7 then      
   set t_tzzs= t_tzzs+1;      
end if;
fetch cur_ftb into t_bkid;/*读取下一行*/
end while;

select t_tzzs;
update ltbk set tzzs=t_tzzs where bkid=7;
close cur_ftb;
END;


